import { useState } from 'react'

const ToggleStatus = ({ todo, editTodo }) => {
  const [isOn, setIsOn] = useState(false)

  const toggle = () => {
    setIsOn(!isOn)
    editTodo(todo, todo.task, !todo.status)
  }

  return (
    <button
        type="button"
        className={`btn ${isOn ? 'btn-primary' : 'btn-secondary'}`}
        onClick={toggle}
      >
        {isOn ? 'ON' : 'OFF'}
      </button>
  );
}

export default ToggleStatus
